const thresh = 1024;
const units = [
  'kB',
  'MB',
  'GB',
  'TB',
  'PB',
  'EB',
  'ZB',
  'YB'
];

/**
 * Convert bytes to human-readable string.
 */
export function getReadableFileSize(bytes: number): string {
  if (bytes < thresh) {
    return bytes + ' B';
  }

  const exp = Math.min(
    units.length,
    Math.floor(
      Math.log(bytes) / Math.log(thresh)
    )
  );

  const result = bytes / Math.pow(2, exp * 10);

  return result.toFixed(1) + ' ' + units[exp - 1];
}

